import {ADD_DATA} from "../constans";
import {SHOW_DATA} from "../constans";


export const addData = (data) => {
    return{
        type: ADD_DATA,
        payload: data
    }
};

export const showData = (data) => {
    return{
        type: SHOW_DATA,
        payload: data
    }
};


export const loadUsers = () => {
    // console.log("loadUser")
    return function (dispatch) {
        axios
            .get(`http://localhost:3000/duser`)
            .then((resp) => {
                console.log("resp", resp);
                dispatch(showData(resp.data))
            })
            .catch((error) => {
                console.log(error);
            });
    };
};
