import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {useParams} from "react-router";
import {userView} from "../redux/actions/action";
import Button from "react-bootstrap/Button";

const Viewuser = () => {


    const dispatch = useDispatch();
    let { id } = useParams();

    const users = useSelector((state) => state.user);
    console.log("users",users.user)

    useEffect(() => {
        dispatch(userView(id));
    }, [ id ]);



    return(
        <div className="container">
            <ul className="list-group w-50" key={users.id}>
                <li className="list-group-item">name: {users.name}</li>
                <li className="list-group-item">email: {users.email}</li>
                <li className="list-group-item">phone: {users.phone}</li>
                <li className="list-group-item">gender: {users.gender}</li>
                <li className="list-group-item">vehicle: {users.vehicle}</li>
                <li className="list-group-item">country: {users.country}</li>
            </ul>
            <Button color="primary" onClick={() => navigate(`/`)}>View</Button>
        </div>
    );
};

export default Viewuser;
