import {ADD_DATA} from "../constans";
import {SHOW_DATA} from "../constans";
import {UPDATE_USER} from "../constans";
import axios from "axios";

const addedData = () => {
    return{
        type: ADD_DATA,
    }
};

const showData = (data1) => {
    return{
        type: SHOW_DATA,
        payload: data1
    }
};

const dataUpdated = () => {
    return {
        type: UPDATE_USER,
    }
};

export const loadUsers = () => {
    // console.log("loadUser")
    return function (dispatch) {
        axios.get("http://localhost:3000/duser")
            .then((resp) => {
                console.log("resp", resp);
                dispatch(showData(resp.data))
            })
            .catch((error) => {
                console.log(error);
            });
    };
};

export const addData = (user) => {
    let { id } = useParams();
    return function (dispatch) {

        if (user.id) {
            axios
                .put(`http://localhost:3000/duser/${id}`,user)
                .then((resp) => {
                    console.log("resp", resp);
                    dispatch(dataUpdated());


                })
                .catch((error) => {
                    console.log(error);
                });

        } else {

            axios
                .post("http://localhost:3000/duser",user)
                .then((resp) => {
                    console.log("resp", resp);
                    dispatch(addedData());
                    dispatch(loadUsers());

                })
                .catch((error) => {
                    console.log(error);
                });

        }

    };
};
