import {ADD_DATA} from "../constans";
import {SHOW_DATA} from "../constans";

const initialState = {
        userData: [],
};

function userReducer(state = initialState, action) {
    switch (action.type) {
        case ADD_DATA:
            return{
                ...state,
            };
        case SHOW_DATA:
            return {
                ...state,
                userData: [...state,action.payload]
            };
        default:
            return state;
    }
}

export default userReducer;
